import React,{useContext} from 'react'
import {View, StyleSheet, FlatList, Text, Button} from 'react-native'
import {ListContext} from "../context/listItem/ListContext";
import {THEME} from "../theme";

export const List = ({data}) => {
    const {list} = useContext(ListContext)

    render_FlatList_footer = (
        <View style={styles.header_footer_style}>
            <Button title="Prev"/>
            <Text style={styles.textStyle}> {list.page+1} </Text>
            <Button title="Next"/>
        </View>
    );
    render_FlatList_header = (
        <View style={styles.header_footer_style}>
            <Text style={styles.textStyle}> Header </Text>
        </View>
    );


    return (
        <View style={styles.wrapper}>

            <FlatList
                data={data}
                keyExtractor={item => item.symbol.toString()}
                renderItem={
                    ({item}) =>
                        <View style={styles.button}>
                            <Text style={styles.title}>{item.symbol}</Text>
                        </View>
                }
                ListHeaderComponent={render_FlatList_header}
                ListFooterComponent={render_FlatList_footer}
            />
        </View>
    )
}

const styles = StyleSheet.create({
    wrapper: {
        padding: 10,
        flex:1
    },
    noItems: {
        fontFamily: 'open-regular',
        textAlign: 'center',
        marginVertical: 10,
        fontSize: 18
    },
    textWrap: {

        paddingVertical: 5,
        alignItems: 'center',
        width: '100%'
    },
    button: {
        margin: 5,
        paddingHorizontal: 20,
        paddingVertical: 10,
        borderRadius: 5,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: THEME.MAIN_COLOR,

    },
    title:{
        color:'#fff',
        fontFamily: 'open-bold',
        fontSize: 20
    },
    header_footer_style: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '100%',
        height: 60,
        backgroundColor: '#CCC',
        alignItems: 'center',
        padding: 10
    },
})
