import React, {useEffect,useContext} from 'react'
import {View, StyleSheet, ActivityIndicator, FlatList, Text} from 'react-native'
import {HeaderButtons, Item} from 'react-navigation-header-buttons'
import {AppHeaderIcon} from '../components/AppHeaderIcon'
import {THEME} from '../theme'
import {List} from "../components/List";
import {ListContext} from "../context/listItem/ListContext";

export const SocketScreen = ({navigation}) => {
    const {get_list,error_list, list}=useContext(ListContext)

    useEffect(() => {
        get_list()
    }, [])


    if (list.loading) {
        return (
            <View style={styles.center}>
                <ActivityIndicator color={THEME.MAIN_COLOR}/>
            </View>
        )
    }


    return (
        <React.Fragment>
            <Text>SOCKET</Text>
            <List data={list.list} />

        </React.Fragment>
    )

}

SocketScreen.navigationOptions = ({navigation}) => ({
    headerTitle: 'Задание 2',
    headerLeft: (
        <HeaderButtons HeaderButtonComponent={AppHeaderIcon}>
            <Item
                title='Toggle Drawer'
                iconName='ios-menu'
                onPress={() => navigation.toggleDrawer()}
            />
        </HeaderButtons>
    )
})

const styles = StyleSheet.create({
    center: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center'
    }
})
