import React, {useEffect, useContext} from 'react'
import {View, StyleSheet, ActivityIndicator, FlatList, Text, Button} from 'react-native'
import {HeaderButtons, Item} from 'react-navigation-header-buttons'
import {AppHeaderIcon} from '../components/AppHeaderIcon'
import {THEME} from '../theme'
import {List} from "../components/List";
import {ListContext} from "../context/listItem/ListContext";

export const ListScreen = ({navigation}) => {
    const {get_list, list} = useContext(ListContext)


    // const openPostHandler = post => {
    //     navigation.navigate('List', {
    //         postId: post.id,
    //         date: post.date,
    //         booked: post.booked
    //     })
    // }



    useEffect(() => {
        get_list()

    }, [])


    if (list.loading) {
        return (
            <View style={styles.center}>
                <ActivityIndicator color={THEME.MAIN_COLOR}/>
            </View>
        )
    }


    return (
        list.error == null ?
            <View style={styles.center}>
                <List data={list.list_page}/>
                <View style={styles.footer}>
                    <Button title="Prev"/>
                    <Text style={styles.textStyle}> {list.page+1} </Text>
                    <Button title="Next"/>
                </View>
            </View>


            :
            <View style={styles.center}>
                {list.error}
            </View>
    )

}

ListScreen.navigationOptions = ({navigation}) => ({
    headerTitle: 'Задание 1',
    headerLeft: (
        <HeaderButtons HeaderButtonComponent={AppHeaderIcon}>
            <Item
                title='Toggle Drawer'
                iconName='ios-menu'
                onPress={() => navigation.toggleDrawer()}
            />
        </HeaderButtons>
    )
})

const styles = StyleSheet.create({
    center: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center'
    },
    footer: {
        position:'absolute',
        bottom:10,
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '100%',
        height: 60,
        backgroundColor: THEME.MAIN_COLOR,
        alignItems: 'center',
        paddingHorizontal: 10,
    },
})
